function doPost(e) {
  if (e && e.parameter) {
    var ss = SpreadsheetApp.openById("11YpNTtyCB8yHErh-nV-6OeCXKjcrUVet7zVgLYB2Lw0"); // Substitua pelo ID da sua planilha
    var sheet = ss.getSheetByName("Returns"); // Substitua pelo nome da sua planilha

    var headers = e.parameter.headers.split(",");
    var values = e.parameter.values.split(",");

    sheet.appendRow(values);

    return ContentService.createTextOutput("Dados enviados com sucesso!");
  } else {
    return ContentService.createTextOutput("Parâmetros inválidos na solicitação.");
  }
}
